package java_July_Batch_10_30;

public class ArrayDiscussion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Single dimention
		 int[] anArray;
		 anArray = new int[10];
		 anArray[0] = 100;

		 //
		 int[] apArray=new int[10];
		 
		 apArray[0] = 200;
		 //
		 
		 System.out.println("Element at index 0: "
                 + anArray[0]);
		 
		 System.out.println("Element at 2nd array index 0: "
                 + apArray[0]);
		 
		 
		 //Declaring an array of different data types
		 
		 byte[] anArrayOfBytes=new byte[13];
		 short[] anArrayOfShorts=new short[13];
		 long[] anArrayOfLongs=new long[15];
		 float[] anArrayOfFloats=new float[20];
		 double[] anArrayOfDoubles=new double[14];
		 boolean[] anArrayOfBooleans=new boolean[20];
		 char[] anArrayOfChars=new char[20];
		
		 
		 //Wrapper
		 Integer[] jlfnsnlfs= new Integer[20];
		 
		 //Non prmitive
		 String[] anArrayOfStrings=new String[45];
		 
		 
		 
		 //Another form bracket can keep near to variables
		 //However, convention discourages this form; the brackets identify the array type and should appear with the type designation
		 byte anArrayOfBytesss[]=new byte[13];
		 
	}

}
